  #{dueAt}, #{issuedReason}, #{txHash}, #{issuedAt}
)

mapper.update:
UPDATE member_nft
SET used_at = #{usedAt}
WHERE id = #{id}
  AND deleted_at IS NULL

UPDATE member_nft
SET member_id = #{toMemberId}
WHERE id = #{nftId} AND member_id = #{fromMemberId}
