mapper.insert:
INSERT INTO pay (
  imp_uid, merchant_uid, amount, pay_method, pg_provider,
  state, paid_at, created_at, reserv_id, member_id, haspay_share
) VALUES (
  #{impUid}, #{merchantUid}, #{amount}, #{payMethod}, #{pgProvider},
  #{state}, #{paidAt}, #{createdAt}, #{reserv.id}, #{member.id}, #{haspayShare}
)

mapper.update:
UPDATE pay
SET state = #{state}
WHERE id = #{id}

UPDATE pay
SET delete_at = NOW()
WHERE id = #{id}
